<!-- Content Wrapper. Contains page content --><div class="content-wrapper">    <!-- Content Header (Page header) -->    <section class="content-header">        <h1>            {menu_title}                    </h1>    </section>    <!-- Main content -->    <section class="content">        <div class="row">            <!-- left column -->            <div class="col-md-12">                <!-- general form elements -->                <div class="box box-primary">                    <div class="box-header">                        <!--                        <h3 class="box-title">Quick Example</h3>-->                        {error_msg}                        {warning}                        {password}                        {nama}                        {email}                        {nik}                        {telp}                        {divisigroup}                        {/error_msg}                    </div><!-- /.box-header -->                    <!-- form start -->                    <form id="form-{file_app}" name="form-{file_app}" action="{action}" method="post" role="form" enctype="multipart/form-data">                        <div class="box-body">                            {post}                            <div class="form-group">                                <label>NIK</label>                                <input type="text" value="{nik}" name="nik" id="nik" class="form-control" placeholder="nik" readonly>                             </div>                            <div class="form-group">                                <label>Nama</label>                                <input type="text" value="{nama}" name="nama" id="nama" class="form-control" placeholder="Name">                            </div>                            <div class="form-group">                                <label for="group">Divisi</label>                                <?php echo form_dropdown('divisigroup', $divisi, array_key_exists('divisigroup', $post[0])?$post[0]['divisigroup']:"", 'class="form-control" id="divisigroup"'); ?>                            </div>                            <div class="form-group">                                <label>Alamat</label>                                <textarea class="form-control" name="alamat">{alamat}</textarea>                            </div>                            <div class="form-group">                                <label for="group">Jenis Kelamin</label>                                <?php echo form_dropdown('jk', $jk, array_key_exists('jk', $post[0])?$post[0]['jk']:"", 'class="form-control" id="jk"'); ?>                            </div>                            <div class="form-group">                                <label>Telepon</label>                                <input type="text" value="{telp}" name="telp" id="telp" class="form-control" placeholder="Nomor Telepon">                            </div>                            <div class="form-group">                                <label>Email</label>                                <input type="text" value="{email}" name="email" id="email" class="form-control" placeholder="Email Address">                            </div>                            <div class="form-group">                                <label>Jabatan</label>                                <input type="text" value="{jabatan}" name="jabatan" id="jabatan" class="form-control" placeholder="Jabatan">                            </div>                            <div class="form-group">                                <label>Password</label>                                <input type="password" name="password" id="password" class="form-control" placeholder="Password">                            </div>                            <div class="form-group">                                <label>Confirm Password</label>                                <input type="password" name="passconf" id="passconf" class="form-control" placeholder="Password Confirmation">                            </div>                            <div class="form-group">                                <label for="group">Status</label>                                <?php echo form_dropdown('status', $status, array_key_exists('status', $post[0])?$post[0]['status']:"", 'class="form-control" id="status"'); ?>                            </div>                            {/post}                        </div><!-- /.box-body -->                        <div class="box-footer">                            <div class="pull-right">                                <button type="submit" class="btn btn-primary">Submit</button>                                <a href="{cancel_btn}" class="btn btn-danger">Cancel</a>                            </div>                        </div>                    </form>                </div><!-- /.box -->            </div><!--/.col -->        </div>   <!-- /.row -->    </section><!-- /.content --></div><!-- /.content-wrapper --><script type="text/javascript">    function popup (url) {        win = window.open(url, "window1", "width=600,height=400,status=yes,scrollbars=yes,resizable=yes");        win.focus();    }</script>